import React from "react";
import "../AppTitle/AppTitle.css";
import App from "../../App";
import { useState, useEffect } from "react";

const AppTitle = () => {
  const fullText1 = "Welcome to";
  const fullText2 = "TerraSomething";
  const [text, setText] = useState("");
  const [isFirstLineComplete, setIsFirstLineComplete] = useState(false);

  useEffect(() => {
    if (!isFirstLineComplete) {
      if (text.length < fullText1.length) {
        setTimeout(() => {
          setText(fullText1.slice(0, text.length + 1));
        }, 150); // Adjust speed of typing here
      } else {
        setIsFirstLineComplete(true);
      }
    } else {
      if (text.length < fullText1.length + fullText2.length + 1) {
        // +1 for the newline or space
        setTimeout(() => {
          setText(
            fullText1 +
              "\n" +
              fullText2.slice(0, text.length - fullText1.length)
          );
        }, 150); // Continue typing the second part
      }
    }
  }, [text, isFirstLineComplete]);

  return (
    <h1>
      {text}
      <span className="cursor"></span>
    </h1>
  );
};

export default AppTitle;
